    .close {
      position: absolute;
      top: 10px;
      right: 20px;
      font-size: 24px;
      cursor: pointer;
    }

    .fondo-difuminado {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-color: rgba(0, 0, 0, 0.5);
      z-index: 999;
    }

    .libro-cuadro {
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background-color: white;
      padding: 20px;
      border-radius: 5px;
      z-index: 1000;
    }
    .libro-imagen {
      width: 100%;
      height: 200px;
      object-fit: contain;
    }
  </style>
</head>
<body>
  <div class="container mt-4 libro-container">
    <h2>Listado de Libros</h2>
  
    <div class="mb-3">
      <label for="nombreBusqueda" class="form-label">Buscar:</label>
      <input type="text" id="nombreBusqueda" [(ngModel)]="nombreBusqueda" class="form-control">
      <div class="d-flex justify-content-between mt-2">
        <button class="btn btn-primary" (click)="buscarLibrosPorNombre()">Buscar</button>
        <button class="btn btn-success" (click)="mostrarFormularioAgregar()">Agregar Libro</button>
      </div>
    </div>

    <div *ngIf="libroBuscado">
      <div class="fondo-difuminado"></div>
      <div class="libro-cuadro">
        <h3>{{ libroBuscado.titulo }}</h3>
        <p>Autor: {{ libroBuscado.autor }}</p>
        <p>Ejemplares disponibles: {{ libroBuscado.ejemplaresDisponibles }}</p>
        <img [src]="getImagenUrl(libroBuscado.imagen)" alt="{{ libroBuscado.titulo }}" class="img-fluid">
        <button class="btn btn-primary" (click)="reservarLibro(libroBuscado)">Reservar</button>
        <button class="btn btn-secondary" (click)="volverALibros()">Volver a la lista</button>
      </div>
    </div>

    <div class="row" *ngIf="libros.length > 0">
      <div class="col-md-4 mb-4" *ngFor="let libro of libros">
        <div class="card h-100 card-libro">
          <img [src]="'http://localhost:8086/api/libros/images/' + libro.imagen" class="card-img-top libro-imagen" alt="{{ libro.titulo }}">
          <div class="card-body">
            <h5 class="card-title">{{ libro.titulo }}</h5>
            <p class="card-text">Autor: {{ libro.autor }}</p>
            <p class="card-text">Ejemplares Disponibles: {{ libro.ejemplaresDisponibles }}</p>
            <button class="btn btn-primary" (click)="reservarLibro(libro)">Reservar</button>
          </div>
        </div>
      </div>
    </div>
  
    <div *ngIf="!libros.length">
      <p>No se encontraron libros.</p>
    </div>
  
    <div *ngIf="mostrarFormulario">
      <div class="fondo-difuminado" (click)="cerrarFormularioAgregar()"></div>
      <div class="modal">
        <div class="modal-content">
          <span class="close" (click)="cerrarFormularioAgregar()">&times;</span>
          <h2>Agregar Nuevo Libro</h2>
          <form (ngSubmit)="agregarLibro()">
            <div class="mb-3">
              <label for="titulo" class="form-label">Título</label>
              <input type="text" class="form-control" id="titulo" [(ngModel)]="nuevoLibro.titulo" name="titulo" required>
            </div>
            <div class="mb-3">
              <label for="autor" class="form-label">Autor</label>
              <input type="text" class="form-control" id="autor" [(ngModel)]="nuevoLibro.autor" name="autor" required>
            </div>
            <div class="mb-3">
              <label for="ejemplares" class="form-label">Ejemplares Disponibles</label>
              <input type="number" class="form-control" id="ejemplares" [(ngModel)]="nuevoLibro.ejemplaresDisponibles" name="ejemplares" required>
            </div>
            <div class="mb-3">
              <label for="imagen" class="form-label">Imagen</label>
              <input type="file" class="form-control" id="imagen" (change)="onFileSelected($event)">
            </div>
            <button type="submit" class="btn btn-primary">Agregar</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</body>
</html>
